/* global TextDecoder */

import { decodeCP437 } from "./cp437-decode.js";

export {
	decodeText
};

function decodeText(value, encoding) {
	if (encoding && encoding.trim().toLowerCase() == "cp437") {
		return decodeCP437(value);
	} else {
		return new TextDecoder(encoding).decode(value);
	}
}
