import { html as s, nothing as e } from "lit-html";
import { safeClassMap as l } from "@arcgis/lumina";
/*! All material copyright ESRI, All Rights Reserved, unless otherwise specified.
See https://github.com/Esri/calcite-design-system/blob/dev/LICENSE.md for details.
v3.0.3 */
const c = {
  validationContainer: "validation-container"
}, m = ({ scale: i, status: a, id: t, icon: n, message: o }) => s`<div class=${l(c.validationContainer)}><calcite-input-message aria-live=polite .icon=${n} id=${t ?? e} .scale=${i} .status=${a}>${o}</calcite-input-message></div>`;
export {
  m as V
};
